query LoadSearchProductsForProductCardsQuery($whereIds: [Int!], $storeId: Int!, $pageSize: Int!, $pickupLocationHasLocker: Boolean!, $targeted: Boolean = false, $itemEnabled: Boolean = false, $retailItemEnabled: Boolean = false, $locationIds: [ID!] = []) {
  products(pageSize: $pageSize, whereIds: $whereIds) {
    products {
      ...IProductFragment
      __typename
    }
    __typename
  }
}

fragment IStoreProductFragment on storeProduct {
  storeProductId
  productId
  onFuelSaver
  onSale
  fuelSaver
  price
  basePrice
  priceMultiple
  isWeighted
  isActive
  isAlcohol
  insertDate
  departmentId
  taxRate
  storeProductDescriptions {
    type
    description
    __typename
  }
  __typename
}

fragment ProductImageFragment on productImage {
  productId
  uri
  size
  type
  isPrimary
  __typename
}

fragment IRetailItemFragment on RetailItem {
  retailItemId
  basePrice
  basePriceQuantity
  retailSection {
    retailAisle {
      sanitizedDescription
      __typename
    }
    __typename
  }
  soldByUnitOfMeasure {
    code
    name
    __typename
  }
  tagPrice
  tagPriceQuantity
  ecommerceTagPrice
  ecommerceTagPriceQuantity
  __typename
}

fragment IItemFragment on Item {
  itemId
  description
  ecommerceStatus
  source
  images {
    imageId
    url
    isPrimaryImage
    __typename
  }
  source
  unitAverageWeight
  retailItems(locationIds: $locationIds) @include(if: $retailItemEnabled) {
    ...IRetailItemFragment
    __typename
  }
  __typename
}

fragment IProductFragment on product {
  productId
  name
  size
  averageWeight
  upc
  brandName
  isNotEligibleForDelivery
  productImages(where: {isPrimary: true}) {
    ...ProductImageFragment
    __typename
  }
  couponProductV4(targeted: $targeted) {
    upc
    couponsV4 {
      couponId
      brand
      offerState
      __typename
    }
    __typename
  }
  productLockers @include(if: $pickupLocationHasLocker) {
    productLockerId
    pickupLocationId
    isLockerEligible
    __typename
  }
  storeProduct(storeId: $storeId, isActive: true) {
    ...IStoreProductFragment
    __typename
  }
  item @include(if: $itemEnabled) {
    ...IItemFragment
    __typename
  }
  __typename
}
